import {withSourceFile}  from "@leight/generator-server";
import {normalize}       from "node:path";
import {type IGenerator} from "../api";

export interface IGeneratorServerSourceParams {
    /**
     * Package used to import schema (usually generated by the leight sdk)
     */
    schemaPackage: string;
    modelName: string;
    /**
     * Package exporting `type PrismaClient`
     */
    prismaPackage: string;
    /**
     * Prisma repository (prismaClient.${prismaModel})
     */
    prismaModel: string;
}

export const generatorServerSource: IGenerator<IGeneratorServerSourceParams> = async (
    {
        barrel,
        file,
        params: {
                    schemaPackage,
                    modelName,
                    prismaPackage,
                    prismaModel,
                },
    }) => {
    withSourceFile()
        .withImports({
            imports: {
                "@leight/container":     [
                    "type IContainer",
                    "ServiceContext",
                ],
                "@leight/query":         [
                    "withCursor",
                ],
                "@leight/prisma":        [
                    "$PrismaClient",
                ],
                "@leight/source":        [
                    "type ISource",
                    "withUpsert",
                ],
                "@leight/source-server": [
                    "AbstractSource",
                ],
                '@leight/trpc-server':[
                    'withSourceProcedure',
                ],
                [schemaPackage]:         [
                    `$${modelName}Source`,
                    `${modelName}QuerySchema`,
                    `type I${modelName}Source`,
                    `type I${modelName}SourceSchema`,
                ],
                [prismaPackage]:         [
                    "type PrismaClient",
                ],
            },
        })
        .withConsts({
            exports: {
                [`${modelName}SourceContext`]: {
                    body: `(container: IContainer) => new ServiceContext<I${modelName}Source>(container, $${modelName}Source)`,
                },
                [`${modelName}SourceProcedure`]: {
                    body: `
withSourceProcedure<I${modelName}SourceSchema>({
    source: $${modelName}Source,
    schema: ${modelName}QuerySchema,
})
                    `,
                },
            },
        })
        .withClasses({
            exports: {
                [`${modelName}BaseSource`]: {
                    extends:    `AbstractSource<I${modelName}SourceSchema>`,
                    implements: `I${modelName}Source`,
                    body:       `
    static inject = [
        $PrismaClient,
    ];

    constructor(
        protected prismaClient: PrismaClient,
    ) {
        super($${modelName}Source);
    }

    async runUpsert(props: ISource.IUpsert<I${modelName}SourceSchema>): Promise<I${modelName}SourceSchema["Entity"]> {
        return this.prismaClient.${prismaModel}.upsert(withUpsert(props));
    }

    async runCount(query?: I${modelName}SourceSchema["Query"]): Promise<number> {
        return this.prismaClient.${prismaModel}.count({
            where: query?.filter,
        });
    }

    async runQuery(query?: I${modelName}SourceSchema["Query"]): Promise<I${modelName}SourceSchema["Entity"][]> {
        return this.prismaClient.${prismaModel}.findMany(withCursor({
            query,
            arg: {
                where:   query?.filter,
                orderBy: query?.sort,
            }
        }));
    }
                    `,
                }
            }
        })
        .saveTo({
            file: normalize(`${process.cwd()}/${file}`),
            barrel,
        });
};
